import "./App.css";
import Search from "./components/Search";
import ProductsList from "./components/ProductsList";
import { useState, useEffect } from "react";
import CartList from "./components/CartList";
import axios from "axios";
import ProductDetails from "./components/ProductDetails";
import ProductsListasadmin from "./components/ProductListAsAdmin.jsx";
import UpdateProduct from "./components/UpdateProduct";
import Login from "./components/Login";
import AddProduct from "./components/Add";

const App = () => {
  const [menuView, setMenuView] = useState(false);
  const [view, setView] = useState("productList");
  const toggleMenu = () => {
    setMenuView(!menuView);
  };
  const switchView = (x) => {
    setView(x);
  };
  const [whey, setWhey] = useState([]);
  const [gainer, setGainer] = useState([]);
  const [preworkout, setPreworkout] = useState([]);
  const [details, setDetails] = useState({});

  const detail = (obj) => {
    setDetails(obj);
    setView("ProductDetails");
  };

  const search = (input) => {
    if (!input) {
      setTrigger(!trigger);
    }

    setData(
      data.filter((e) => e.name.toLowerCase().includes(input.toLowerCase()))
    );
  };

  const [cart, setCart] = useState([]);
  console.log(cart);
  const cartFunc = (obj) => {
    setCart([...cart, obj]);
  };

  const emptyCart = () => {
    setCart([]);
  };
  const removeCart = (index) => {
    var newState = [...cart];
    newState.splice(index, 1);
    setCart(newState);
  };

  const [data, setData] = useState([]);
  const [trigger, setTrigger] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/products")
      .then((resp) => {
        setData(resp.data);
        setWhey(
          resp.data.filter((e) => {
            return e.categories === "whey";
          })
        );
        setGainer(
          resp.data.filter((e) => {
            return e.categories === "mass gainer";
          })
        );
        setPreworkout(
          resp.data.filter((e) => {
            return e.categories === "pre workout";
          })
        );
      })
      .catch((err) => console.log(err));
  }, [trigger]);

  //addproduct:
  const addProduct = (obj) => {
    axios
      .post(`http://localhost:5000/api/products/add`, obj)
      .then((result) => {
        setTrigger(!trigger)
        switchView("ProductsListasadmin")
      })
      .catch((error) => {
        console.log(error);
      });
  };

  //updateproduct:
  const [id, setid] = useState("");
  const setId = (id) => {
    setid(id);
  };
  const uppdateproduct = (obj) => {
    axios
      .post(`http://localhost:5000/api/products/update/${id}`, obj)
      .then((result) => {
        switchView("ProductsListasadmin")
        setTrigger(!trigger)
      })
      .catch((error) => {
        console.log(error);
      });
  };

  //delete product :
  const deleteProduct = (obj) => {
    axios
      .delete(`http://localhost:5000/api/products/delete/${obj._id}`)
      .then((result) => {
        setTrigger(!trigger)
        
      })
      .catch((error) => {
        console.log(error);
      });
  };

  // login
  const verifAdmin = (obj) => {
    axios
      .get(`http://localhost:5000/api/users/${obj._id}`)
      .then((result) => {
        if (
          result.data.email === obj.email &&
          result.data.password === obj.password
        ) {
          return true;
        } else {
          return false;
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div className="App">
      <div className="nav">
        <img
          className="logo"
          onClick={() => {
            setTrigger(!trigger);
            switchView("productList");
          }}
          src="https://govindjee.store/cdn/shop/collections/high-protein-906413.jpg?v=1679136846"
        />
        <span className="logo"></span>
        {view === "productList" && <Search search={search} />}
        {view === "productList" && (
          <span className="items" onClick={toggleMenu}>
            Categories &#9660;
          </span>
        )}
        <span
          className="items"
          onClick={() => {
            switchView("cart");
          }}
        >
          🛒
        </span>
        <span
          className="items"
          onClick={() => {
            switchView("login");
          }}
        >
          login as admin
        </span>
      </div>
      {menuView && (
        <div className="menu">
          <span
            className="menu-item"
            onClick={() => {
              setData(whey);
            }}
          >
            Whey
          </span>
          <span
            className="menu-item"
            onClick={() => {
              setData(gainer);
            }}
          >
            Mass gainer
          </span>
          <span
            className="menu-item"
            onClick={() => {
              setData(preworkout);
            }}
          >
            Pre-workout
          </span>
        </div>
      )}
      {view === "productList" && (
        <ProductsList detail={detail} data={data} cartFunc={cartFunc} />
      )}
      {view === "cart" && (
        <CartList cartData={cart} empty={emptyCart} removeCart={removeCart} />
      )}
      {view === "ProductDetails" && <ProductDetails details={details} />}
      {view === "addproduct" && <AddProduct addproduct={addproduct} />}
      {view === "updateproject" && (
        <UpdateProduct id={id} uppdateproduct={uppdateproduct} />
      )}
      {view === "login" && (
        <Login verifAdmin={verifAdmin} switchView={switchView} />
      )}
            {view === "addproduct" && (
        <AddProduct addProduct={addProduct} switchView={switchView} />
      )}
      {view === "ProductsListasadmin" && (
        <ProductsListasadmin
          setId={setId}
          data={data}
          switchView={switchView}
          uppdateproduct={uppdateproduct}
          deleteProduct={deleteProduct}
        />
      )}
    </div>
  );
};

export default App;
