import React from "react";

const ProductsListasadmin = (props) => {
  return (
    <div class="card">
      {props.data &&
        props.data.map((e, i) => {
          return (
            <div class="card-border-top">
  <img class="img" src={e.imageUrl} onClick={() => props.detail(e)} />
<span> {e.name}</span>
<p class="job"> {e.price}</p>
              <button
                onClick={() => {
                  props.switchView("updateproject");
                  props.setId(e.id);
                }}
              >
                update
              </button>
              <button
                onClick={() => {
                  props.deleteProduct(e, i);
                }}
              >
                delete
              </button>
            </div>
          );
        })}
      <button
        onClick={() => {
          props.switchView("addproduct");
        }}
      ></button>
    </div>
  );
};

export default ProductsListasadmin;
