import React from "react";
const Uppdateproduct = (props) => {
  return (
    <div class="update">
      <input id="name" className="form-group" autocomplete="off" placeholder="Name:" type="text" />
      <br />
      <input id="price" className="form-group" autocomplete="off" placeholder="price:" type="price" />
      <br />
      <label></label>

      <input id="desc" className="form-group" autocomplete="off" placeholder="description:" type="text" />
      <br />
      <label>category:</label>

      <input id="cat" className="form-group" autocomplete="off" placeholder="" type="text" />
      <br />
      <label>image url:</label>

      <input id="img" className="form-group" autocomplete="off" placeholder="" type="text" />
      <button
        onClick={props.uppdateproduct(props.id, {
          name: document.getElementById("name"),
          price: document.getElementById("price"),
          description: document.getElementById("desc"),
          categories: document.getElementById("cat"),
          imageUrl: document.getElementById("name"),
        })}
      >
        update
      </button>
    </div>
  );
};

export default Uppdateproduct;
